import os
import json
import urllib3
import boto3


class S3Client:

    def __init__(self):
        self.s3 = boto3.resource('s3')
        self.bucket_name = os.environ.get("email_template_bucket")

    def read_file(self, file_path):
        return str(self.s3.Object(self.bucket_name, file_path).get()['Body'].read())